from enum import StrEnum
from typing import Annotated

from pytoniq_core.boc import Cell
from src.utils.str_tools import b64str_to_bytes, bytes_to_b64str


class WalletVersion(StrEnum):

    V2R1 = "v2r1"
    V2R2 = "v2r2"
    V3R1 = "v3r1"
    V3R2 = "v3r2"
    V4R1 = "v4r1"
    V4R2 = "v4r2"
    V5 = "v5"
    HV2 = "hv2"
    HV3 = "hv3"


WALLET_CODES: dict[WalletVersion, str] = {
    WalletVersion.V2R1: "te6cckEBAQEAVwAAqv8AIN0gggFMl7qXMO1E0NcLH+Ck8mCDCNcYINMf0x8B+CO78mPtRNDTH9P/0VExuvKhA/kBVBBC+RDyovgAApMg10qW0wfUAvsA6NGkyMsfy//J7VShNwu2",  # noqa
    WalletVersion.V2R2: "te6cckEBAQEAYwAAwv8AIN0gggFMl7ohggEznLqxnHGw7UTQ0x/XC//jBOCk8mCDCNcYINMf0x8B+CO78mPtRNDTH9P/0VExuvKhA/kBVBBC+RDyovgAApMg10qW0wfUAvsA6NGkyMsfy//J7VQETNeh",  # noqa
    WalletVersion.V3R1: "te6cckEBAQEAYgAAwP8AIN0gggFMl7qXMO1E0NcLH+Ck8mCDCNcYINMf0x/TH/gjE7vyY+1E0NMf0x/T/9FRMrryoVFEuvKiBPkBVBBV+RDyo/gAkyDXSpbTB9QC+wDo0QGkyMsfyx/L/8ntVD++buA=",  # noqa
    WalletVersion.V3R2: "te6cckEBAQEAcQAA3v8AIN0gggFMl7ohggEznLqxn3Gw7UTQ0x/THzHXC//jBOCk8mCDCNcYINMf0x/TH/gjE7vyY+1E0NMf0x/T/9FRMrryoVFEuvKiBPkBVBBV+RDyo/gAkyDXSpbTB9QC+wDo0QGkyMsfyx/L/8ntVBC9ba0=",  # noqa
    WalletVersion.V4R1: "",  # noqa
    WalletVersion.V4R2: "",  # noqa
    WalletVersion.V5: "",  # noqa
    WalletVersion.HV2: "",  # noqa
    WalletVersion.HV3: "",  # noqa
}

WALLET_VERSIONS_DICT: Annotated[
    dict[str, WalletVersion], "Keys are wallet code cell hashes, values are wallet versions."
] = {
    bytes_to_b64str(Cell.one_from_boc(b64str_to_bytes(value)).hash): key
    for key, value in WALLET_CODES.items()
}


def get_wallet_version(wallet_code_cell_hash: str) -> WalletVersion | None:
    return WALLET_VERSIONS_DICT.get(wallet_code_cell_hash, None)
